from django.contrib.auth import get_user_modelfrom core.models import Recipe, Tag, Ingredientdef create_sample_user(email='test@example.com', password='password'):    return get_user_model().objects.create_user(        email=email,        password=password    )def create_sample_tag(user, name='Sample Tag'):    """ Create Sample tag"""    return Tag.objects.create(user=user, name=name)def create_sample_ingredient(user, name='Sample Ingredient'):    """Create a sample recipe Ingredient"""    return Ingredient.objects.create(user=user, name=name)def sample_recipe(user, **params):    """Create and return a sample recipe"""    default = {        'title': 'sample recipe',        'time_minutes': 10,        'price': 5.00    }    default.update(params)    return Recipe.objects.create(user=user, **default)